<?php

namespace Blogger\BlogBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * TagRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TagRepository extends EntityRepository
{
    public function getTagList()
    {
        $qb = $this->createQueryBuilder('t')
            ->select('t', 'p')
            ->leftJoin('t.posts', 'p')
            ->addOrderBy('t.name', 'ASC');

        return $qb->getQuery()
            ->getResult();
    }

    public function geTagListWithPosts() {
        $qb = $this->createQueryBuilder('t')
            ->select('t', 'p')
            ->innerJoin('t.posts', 'p')
            ->where('p.posted = 1')
            ->addOrderBy('t.name', 'ASC');

        return $qb->getQuery()
            ->getResult();
    }

    public function isTagUnique($name, $slug) {
        $qb = $this->createQueryBuilder('t')
            ->select('t')
            ->where('t.slug = :slug')
            ->orWhere('t.name = :name')
            ->setParameters(
                array(
                    'slug' => $slug,
                    'name' => $name
                )
            );
        if($qb->getQuery()->getResult())
            return false;
        else
            return true;
    }

    public function getTagWeights()
    {
        $tags = $this->geTagListWithPosts();
        if (empty($tags))
            return $tags;

        // Shuffle the tags
        shuffle($tags);
        $max = 0;
        $min = 10000000;

        foreach($tags as $tag) {
            $postCount = $tag->getPostCount();
            if($postCount < $min)
                $min = $postCount;
            if($postCount > $max)
                $max = $postCount;
        }

        foreach($tags as $tag) {
            $postCount = $tag->getPostCount();
            $maxFont = 24;
            $minFont = 12;
            $weight = ($postCount-$min+1) / ($max-$min+1) * ($maxFont - $minFont) + $minFont;
            $tag->setWeight($weight);
        }

        return $tags;
    }

    public function findOneByName($name) {
        $qb = $this->createQueryBuilder('t')
            ->select('t')
            ->where('t.name = :name')
            ->setParameter('name', $name);
        return $qb->getQuery()->getOneOrNullResult();
    }

    public function findBySlug($slug) {
        $qb = $this->createQueryBuilder('t')
            ->select('t')
            ->where('t.slug = :slug')
            ->setParameter('slug', $slug);
        return $qb->getQuery()->getOneOrNullResult();
    }
}